import React from 'react'

type TabProps = {
    isActive?:boolean
    toogleTab?: (event: any) => void;
    // maxWidth:string;
    // isCentered?:boolean;
    // isCenteredX?:boolean;

}

const Tab:React.FC<TabProps> = (props: React.PropsWithChildren<TabProps>) => {

    // const numberTabCount = () => {
    //     const sections: JSX.Element[] = [];
